                        "items": {
                            "type": "object",
                            "title": "Contact",
                            "properties": {
                                "Filter": {
                                    "type": "string",
                                    "description": "Keywords in lower case seperated by space, e.g. keyword1 keywords",
                                    "default": ""
                                },
                                "AvatarImage": {
                                    "type": "string",
                                    "format": "pageImage",
                                    "default": "/SmartAdmin451/img/demo/avatars/avatar-d.png",
                                    "options": {
                                        "upload": {
                                            "width": 50,
                                            "height": 50,
                                            "upload_handler": "uploadHandler",
                                            "getPageConfig_handler": "getPageConfig"
                                        }
                                    },
                                    "links": [
                                        {
                                            "href": "{{self}}"
                                        }
                                    ]
                                },
