                        "type": "array",
                        "minItems": 0,
                        "maxItems": 1,
                        "format": "grid",
                        "title": "Settings",
                        "items": {
                            "type": "object",
                            "title": "Settings",
                            "properties": {
                                "Href": {
                                    "type": "string",
                                    "default": "#"
                                }
                            }
                        }
                    },
