                        "type": "array",
                        "format": "table",
                        "title": "Segments",
                        "items": {
                            "type": "object",
                            "title": "Segment",
                            "properties": {
                                "Type": {
                                    "enum": [
                                        "chat-segment-sent",
                                        "chat-segment-get",
                                        ""
                                    ],
                                    "type": "string",
                                    "default": "Page"
                                },
                                "Position": {
                                    "enum": [
                                        "chat-start",
                                        "chat-end",
                                        ""
                                    ],
                                    "type": "string",
                                    "default": "Page"
                                },

