                                                "default": ""
                                            },
                                            "Position": {
                                                "enum": [
                                                    "text-right",
                                                    ""
                                                ],
                                                "type": "string",
                                                "default": ""
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    },
                    "MessageTypeText": {
                        "type": "string",
                        "default": "Type your message here..."
                    },
                    "MessageMoreOptions": {
                        "type": "array",
                        "minItems": 0,
                        "maxItems": 1,
                        "format": "grid",
                        "title": "Settings",
                        "items": {
                            "type": "object",
                            "title": "Settings",
                            "properties": {
                                "Text": {
                                    "type": "string",
                                    "default": "More options"
                                },
                                "Href": {
                                    "type": "string",
                                    "default": "javascript:void(0);"
                                }
                            }
                        }
                    },
                    "MessageAttach": {
                        "type": "array",
                        "minItems": 0,
                        "maxItems": 1,
                        "format": "grid",
                        "title": "Settings",
                        "items": {
                            "type": "object",
                            "title": "Settings",
                            "properties": {
                                "Text": {
                                    "type": "string",
                                    "default": "Attach files"
                                },
                                "Href": {
                                    "type": "string",
                                    "default": "javascript:void(0);"
                                }
                            }
                        }
                    },
                    "MessageInsertPhoto": {
                        "type": "array",
                        "minItems": 0,
                        "maxItems": 1,
                        "format": "grid",
                        "title": "Settings",
                        "items": {
                            "type": "object",
