                            "properties": {
                                "Text": {
                                    "type": "string",
                                    "default": "Insert photo"
                                },
                                "Href": {
                                    "type": "string",
                                    "default": "javascript:void(0);"
                                }
                            }
                        }
                    },
                    "MessageSendHref": {
                        "type": "string",
                        "default": "javascript:void(0);"
                    }
                }
