STOPString003:
{
    "type": "object",
    "title": "Template",
    "properties": {
        "Breadcrumb": {
            "type": "array",
            "minItems": 0,
            "maxItems": 1,
            "format": "grid",
            "title": "Breadcrumb",
            "items": {
                "type": "object",
                "title": "Breadcrumb",
                "properties": {
                    "Items": {
                        "type": "array",
                        "format": "table",
                        "title": "Items",
                        "items": {
                            "type": "object",
                            "title": "Item",
                            "properties": {
                                "Href": {
                                    "type": "string",
                                    "default": "#"
                                },
                                "Title": {
                                    "type": "string",
                                    "default": "Page"
                                }
                            }
                        }
                    },
                    "ActiveItems": {
                        "type": "array",
                        "minItems": 1,
                        "maxItems": 1,
                        "format": "table",
                        "title": "Active Item",
                        "items": {
                            "type": "object",
                            "title": "Item",
                            "properties": {
                                "Title": {
                                    "type": "string",
                                    "default": "Page"
                                }
                            }
                        }
                    },
                    "RightContent": {
                        "type": "string",
                        "description": "Content right of breadcrumb",
                        "default": "<li class='position-absolute pos-top pos-right d-none d-sm-block'><span class='js-get-date'></span></li>"
                    }
                }
            }
        },

        "Header": {
            "type": "array",
            "minItems": 0,
            "maxItems": 1,
            "format": "grid",
            "title": "Header",
            "items": {
                "type": "object",
                "title": "Header",
                "properties": {
                    "Title": {
                        "type": "string",
                        "default": "<i class='fal fa-info-circle'></i> Page Title"
                    },
                    "SubTitle": {
                        "type": "string",
                        "default": ""
                    }
                }
            }
        },

        "MainSectionName": {
            "type": "string",
            "default": "Main"
        }
    }
}

